Drupal.behaviors.formPopup = function( ){
  $('#popups [data-form-id]').each( function(){
    var $form = $(this);
    $form.bubblepopup();
    
    $('a[href='+$form.attr('data-form-id')+']').each(function(){
      var $a = $(this);
      var dir = $a.attr('data-popup-dir');
      dir = dir? dir : 'top';
      $(this).click(function(event) {
        $form.bubblepopup('toggle', $a, dir);
        event.stopPropagation();
        return false;
      });
    });
  });
};


